CREATE TABLE "odpowiedzi" (
	"id_odpowiedzi"	INTEGER NOT NULL UNIQUE,
	"id_pytania"	INTEGER NOT NULL,
	"odpowiedz_a"	TEXT NOT NULL,
	"odpowiedz_b"	TEXT NOT NULL,
	"odpowiedz_c"	TEXT NOT NULL,
	"odpowiedz_d"	TEXT NOT NULL,
	"prawidlowa"	TEXT NOT NULL,
	FOREIGN KEY("id_pytania") REFERENCES "pytania"("id_pytania"),
	PRIMARY KEY("id_odpowiedzi" AUTOINCREMENT)
);

CREATE TABLE "pytania" (
	"id_pytania"	INTEGER NOT NULL UNIQUE,
	"tresc"	TEXT NOT NULL UNIQUE,
	"zalacznik"	TEXT,
	PRIMARY KEY("id_pytania" AUTOINCREMENT)
);



